var File = require('fs'),
	Path = require('path');
	
module.exports.isFunc = function isFunc (func) {
	return typeof func == 'function';
};
module.exports.isDir = function isDir (dirname) {
	var dirExists = Path.existsSync(dirname);
		dirIsDir = dirExists ? File.statSync(dirname).isDirectory() : false;
	return dirIsDir;
};
module.exports.isFile = function isFile (filename) {
	var fileExists = Path.existsSync(filename),
		file = fileExists ? File.statSync(filename).isFile() : false;
	return file;
};